import { GET_PROFILE, PROFILE_ERROR, CLEAR_PROFILE } from '../actions/types';
const initialstate = {
  profile: {},
  profiles: [],
  loading: true,
  repos: [],
  error: {}
};

export default (state = initialstate, action) => {
  const { type, payload } = action;
  switch (type) {
    case GET_PROFILE:
      return {
        ...state,
        profile: payload,
        loading: false
      };
    case PROFILE_ERROR:
      return {
        ...state,
        error: payload,
        loading: false
      };
    case CLEAR_PROFILE:
      return {
        ...state,
        profile:{},
        repos: [],
        loading: false
      };
    default:
      return state;
  }
};
