import { GET_POSTS, POST_ERROR, UPDATE_LIKE } from "../actions/types";

const initialState = {
  posts: [],
  post: null,
  loading: true,
  error: {}
};

export default function(state = initialState, action) {
  const { payload, type } = action;
  switch (type) {
    case GET_POSTS:
      return {
        ...state,
        posts: payload,
        loading: false
      };
    case POST_ERROR:
      return {
        ...state,
        error: payload,
        loading: false
      };
    case UPDATE_LIKE:
      return {
        ...state,
        posts: state.posts.map(post =>
          post._id === payload.id ? { ...posts, likes: payload.likes } : post
        )
      };
    default:
      return state;
  }
}
