import React, { Fragment, useState } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { setAlert } from "../../actions/alert";

const Register = ({ alertAction }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    password2: ""
  });
  const { name, email, password, password2 } = formData;
  const onChange = e => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };
  console.log(alertAction);

  const onSubmit = async e => {
    e.preventDefault();
    if (password !== password2) {
      alertAction("Password Do not Match", "danger");
    } else {
      const newUser = {
        name,
        email,
        password
      };

      try {
        const config = {
          headers: {
            "Content-Type": "application/json"
          }
        };
        const body = JSON.stringify(newUser);
        const res = await axios.post("/api/users", body, config);
        console.log(res);
      } catch (err) {
        if (err) {
          console.log(err.response.data);
        }
      }
    }
  };
  return (
    <Fragment>
      <h1 className="large text-primary">Sign Up</h1>
      <p className="lead">
        <i className="fas fa-user"> Create Your Account!!!</i>
      </p>
      <form onSubmit={e => onSubmit(e)} className="form">
        <div className="form-group">
          <input
            type="text"
            placeholder="Name"
            name="name"
            value={name}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="email"
            placeholder="Email"
            name="email"
            value={email}
            onChange={e => onChange(e)}
          />
        </div>
        <small className="form-text">
          This site uses Gravatar,so if you want a profile image, use gravatar
          email.
        </small>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"
            minLength="6"
            name="password"
            value={password}
            onChange={e => onChange(e)}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Confirm Password"
            minLength="6"
            name="password2"
            value={password2}
            onChange={e => onChange(e)}
          />
        </div>
        <div className="form-group">
          <input type="submit" value="Register" className="btn btn-primary" />
        </div>
      </form>
      <p className="my-1">
        Already have an Account link <a href="login.html">Sign In</a>
      </p>
    </Fragment>
  );
};
const mapStateToProps = state => {
  return console.log("map state");
};
const mapDispatchToProps = dispatch => {
  return {
    alertAction: () => {
      dispatch(setAlert());
    }
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Register);
