import React, { Fragment } from "react";
import Moment from "react-moment";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { addLike, removeLike, deletePost } from "../../actions/post";

const PostItems = ({
  post: { _id, name, avatar, text, user, likes, comments, date },
  showActions
}) => {
  const dispatch = useDispatch(() => addLike());
  const auth = useSelector(state => state.auth);
  return (
    <div class='post bg-white p-1 my-1'>
      <div>
        <Link to={`/profile/${user}`}>
          <img class='round-img' src={avatar} alt='' />
          <h4>{name}</h4>
        </Link>
      </div>
      <div>
        <p class='my-1'>{text}</p>
        <p class='post-date'>
          Posted on <Moment format='YYYY-MM-DD'>{date}</Moment>
        </p>
        {!showActions && (
          <Fragment>
            <button
              type='button'
              onClick={e => dispatch(addLike(_id))}
              class='btn btn-light'
            >
              <i class='fas fa-thumbs-up'></i>{" "}
              {likes.length > 0 && <span>{likes.length}</span>}
            </button>
            <button
              type='button'
              onClick={e => dispatch(removeLike(_id))}
              class='btn btn-light'
            >
              <i class='fas fa-thumbs-down'></i>
            </button>
            <Link to={`/posts/${_id}`} class='btn btn-primary'>
              Discussion{" "}
              {comments.length > 0 && (
                <span class='comment-count'>{comments.length}</span>
              )}
            </Link>
            {!auth.loading && user === auth.user._id && (
              <button
                type='button'
                onClick={e => dispatch(deletePost(_id))}
                class='btn btn-danger'
              >
                <i class='fas fa-times'></i>
              </button>
            )}
          </Fragment>
        )}
      </div>
    </div>
  );
};
PostItems.defaultProp = {
  showActions: true
};
export default PostItems;
