import React, { Fragment, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPosts } from "../../actions/post";
import Spinner from "../layouts/Spinner";
import PostItems from "./PostItems";

const Posts = () => {
  const dispatch = useDispatch(() => getPosts());
  const getPost = useSelector(state => state.post);
  const { posts, loading } = getPost;
  useEffect(() => dispatch(getPosts()), [getPosts]);
  return loading ? (
    <Spinner />
  ) : (
    <Fragment>
      <h1 className='large text-primary'>Posts</h1>
      <p className='lead'>
        <i className='fas fa-user'></i> Welcome to Community...
      </p>
      {/*postform*/}
      <div className='posts'>
        {posts.map(post => (
          <PostItems key={post._id} post={post} />
        ))}
      </div>
    </Fragment>
  );
};

const mapStateToProps = state => ({ post: state.post });
export default connect(
  mapStateToProps,
  { getPosts }
)(Posts);
